import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useSocket } from '../context/SocketContext';
import { useAuth } from '../context/AuthContext';
import { 
  FiSend, 
  FiSkipForward, 
  FiFlag, 
  FiUser, 
  FiSearch,
  FiMessageCircle,
  FiAlertTriangle,
  FiHeart,
  FiSettings
} from 'react-icons/fi';
import LoadingSpinner from '../components/LoadingSpinner';
import toast from 'react-hot-toast';

const Chat = () => {
  const { user } = useAuth();
  const {
    connected,
    currentChat,
    messages,
    isTyping,
    chatStatus,
    findChat,
    sendMessage,
    skipChat,
    reportUser,
    startTyping,
    stopTyping
  } = useSocket();

  const [messageInput, setMessageInput] = useState('');
  const [showReportModal, setShowReportModal] = useState(false);
  const [showInterestsModal, setShowInterestsModal] = useState(false);
  const [selectedInterests, setSelectedInterests] = useState([]);
  const [typingTimeout, setTypingTimeout] = useState(null);

  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const interestOptions = [
    'Music', 'Movies', 'Sports', 'Gaming', 'Technology', 'Travel',
    'Food', 'Art', 'Books', 'Photography', 'Fitness', 'Fashion',
    'Science', 'Politics', 'Business', 'Education', 'Health', 'Nature'
  ];

  const reportReasons = [
    { value: 'inappropriate_content', label: 'Inappropriate Content' },
    { value: 'harassment', label: 'Harassment' },
    { value: 'spam', label: 'Spam' },
    { value: 'fake_profile', label: 'Fake Profile' },
    { value: 'other', label: 'Other' }
  ];

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Focus input when chat starts
  useEffect(() => {
    if (chatStatus === 'connected') {
      inputRef.current?.focus();
    }
  }, [chatStatus]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (messageInput.trim() && chatStatus === 'connected') {
      sendMessage(messageInput);
      setMessageInput('');
      stopTyping();
    }
  };

  const handleInputChange = (e) => {
    setMessageInput(e.target.value);

    // Handle typing indicators
    if (chatStatus === 'connected') {
      startTyping();

      // Clear existing timeout
      if (typingTimeout) {
        clearTimeout(typingTimeout);
      }

      // Set new timeout to stop typing
      const timeout = setTimeout(() => {
        stopTyping();
      }, 1000);

      setTypingTimeout(timeout);
    }
  };

  const handleFindChat = () => {
    if (selectedInterests.length > 0) {
      findChat(selectedInterests);
    } else {
      findChat();
    }
    setShowInterestsModal(false);
  };

  const handleSkipChat = () => {
    skipChat();
    toast.info('Looking for a new chat partner...');
  };

  const handleReport = (reason, description = '') => {
    reportUser(reason, description);
    setShowReportModal(false);
    handleSkipChat();
  };

  const toggleInterest = (interest) => {
    setSelectedInterests(prev => 
      prev.includes(interest)
        ? prev.filter(i => i !== interest)
        : [...prev, interest]
    );
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  if (!connected) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center text-white">
          <LoadingSpinner size="lg" color="white" />
          <p className="mt-4 text-lg">Connecting to chat server...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-purple-600 to-blue-800">
      <div className="container mx-auto px-4 py-6 h-screen flex flex-col">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-lg p-4 mb-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-3 h-3 rounded-full bg-green-500"></div>
              <span className="font-medium text-gray-800">
                {chatStatus === 'idle' && 'Ready to chat'}
                {chatStatus === 'searching' && 'Finding someone for you...'}
                {chatStatus === 'connected' && 'Connected with a stranger'}
                {chatStatus === 'ended' && 'Chat ended'}
              </span>
            </div>

            <div className="flex items-center space-x-2">
              {chatStatus === 'idle' && (
                <>
                  <button
                    onClick={() => setShowInterestsModal(true)}
                    className="btn btn-sm bg-gray-100 text-gray-700 hover:bg-gray-200 flex items-center space-x-1"
                  >
                    <FiSettings className="text-sm" />
                    <span>Interests</span>
                  </button>
                  <button
                    onClick={handleFindChat}
                    className="btn btn-primary btn-sm flex items-center space-x-1"
                  >
                    <FiSearch className="text-sm" />
                    <span>Find Chat</span>
                  </button>
                </>
              )}

              {chatStatus === 'connected' && (
                <>
                  <button
                    onClick={() => setShowReportModal(true)}
                    className="btn btn-sm bg-red-100 text-red-700 hover:bg-red-200 flex items-center space-x-1"
                  >
                    <FiFlag className="text-sm" />
                    <span>Report</span>
                  </button>
                  <button
                    onClick={handleSkipChat}
                    className="btn btn-sm bg-yellow-100 text-yellow-700 hover:bg-yellow-200 flex items-center space-x-1"
                  >
                    <FiSkipForward className="text-sm" />
                    <span>Skip</span>
                  </button>
                </>
              )}
            </div>
          </div>
        </div>

        {/* Chat Area */}
        <div className="flex-1 bg-white rounded-lg shadow-lg flex flex-col overflow-hidden">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {chatStatus === 'idle' && (
              <div className="text-center py-12">
                <FiMessageCircle className="text-6xl text-gray-300 mx-auto mb-4" />
                <h3 className="text-xl font-semibold text-gray-600 mb-2">
                  Welcome to Anonymous Talk
                </h3>
                <p className="text-gray-500 mb-6">
                  Click "Find Chat" to connect with a random stranger and start chatting!
                </p>
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 max-w-md mx-auto">
                  <h4 className="font-medium text-blue-800 mb-2">Safety Tips:</h4>
                  <ul className="text-sm text-blue-700 space-y-1">
                    <li>• Never share personal information</li>
                    <li>• Report inappropriate behavior</li>
                    <li>• Be respectful to others</li>
                    <li>• Have fun and stay safe!</li>
                  </ul>
                </div>
              </div>
            )}

            {chatStatus === 'searching' && (
              <div className="text-center py-12">
                <LoadingSpinner size="lg" />
                <h3 className="text-xl font-semibold text-gray-600 mb-2 mt-4">
                  Finding someone for you...
                </h3>
                <p className="text-gray-500">
                  This usually takes just a few seconds
                </p>
              </div>
            )}

            {messages.map((message) => (
              <motion.div
                key={message.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
                className={`flex ${message.isOwn ? 'justify-end' : 'justify-start'}`}
              >
                {message.isSystem ? (
                  <div className="bg-gray-100 text-gray-600 px-4 py-2 rounded-lg text-sm text-center max-w-md">
                    {message.message}
                  </div>
                ) : (
                  <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                    message.isOwn
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-200 text-gray-800'
                  }`}>
                    <p className="break-words">{message.message}</p>
                    <p className={`text-xs mt-1 ${
                      message.isOwn ? 'text-blue-100' : 'text-gray-500'
                    }`}>
                      {formatTime(message.timestamp)}
                    </p>
                  </div>
                )}
              </motion.div>
            ))}

            {isTyping && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="flex justify-start"
              >
                <div className="bg-gray-200 text-gray-600 px-4 py-2 rounded-lg">
                  <div className="flex space-x-1">
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                  </div>
                </div>
              </motion.div>
            )}

            <div ref={messagesEndRef} />
          </div>

          {/* Message Input */}
          {chatStatus === 'connected' && (
            <div className="border-t border-gray-200 p-4">
              <form onSubmit={handleSendMessage} className="flex space-x-2">
                <input
                  ref={inputRef}
                  type="text"
                  value={messageInput}
                  onChange={handleInputChange}
                  placeholder="Type your message..."
                  className="flex-1 input"
                  maxLength={500}
                />
                <button
                  type="submit"
                  disabled={!messageInput.trim()}
                  className="btn btn-primary px-4 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <FiSend />
                </button>
              </form>
            </div>
          )}
        </div>

        {/* Interests Modal */}
        <AnimatePresence>
          {showInterestsModal && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
              onClick={() => setShowInterestsModal(false)}
            >
              <motion.div
                initial={{ scale: 0.9, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.9, opacity: 0 }}
                className="bg-white rounded-lg p-6 max-w-md w-full"
                onClick={(e) => e.stopPropagation()}
              >
                <h3 className="text-lg font-semibold mb-4">Select Your Interests</h3>
                <p className="text-sm text-gray-600 mb-4">
                  Choose topics you're interested in to find like-minded people
                </p>

                <div className="grid grid-cols-2 gap-2 mb-6 max-h-60 overflow-y-auto">
                  {interestOptions.map((interest) => (
                    <button
                      key={interest}
                      type="button"
                      onClick={() => toggleInterest(interest)}
                      className={`px-3 py-2 text-sm rounded-md border transition-colors ${
                        selectedInterests.includes(interest)
                          ? 'bg-blue-100 border-blue-300 text-blue-700'
                          : 'bg-gray-50 border-gray-300 text-gray-700 hover:bg-gray-100'
                      }`}
                    >
                      {interest}
                    </button>
                  ))}
                </div>

                <div className="flex space-x-3">
                  <button
                    onClick={() => setShowInterestsModal(false)}
                    className="flex-1 btn btn-secondary"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleFindChat}
                    className="flex-1 btn btn-primary"
                  >
                    Find Chat
                  </button>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Report Modal */}
        <AnimatePresence>
          {showReportModal && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
              onClick={() => setShowReportModal(false)}
            >
              <motion.div
                initial={{ scale: 0.9, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.9, opacity: 0 }}
                className="bg-white rounded-lg p-6 max-w-md w-full"
                onClick={(e) => e.stopPropagation()}
              >
                <div className="flex items-center space-x-2 mb-4">
                  <FiAlertTriangle className="text-red-500 text-xl" />
                  <h3 className="text-lg font-semibold">Report User</h3>
                </div>

                <p className="text-sm text-gray-600 mb-4">
                  Please select the reason for reporting this user:
                </p>

                <div className="space-y-2 mb-6">
                  {reportReasons.map((reason) => (
                    <button
                      key={reason.value}
                      onClick={() => handleReport(reason.value)}
                      className="w-full text-left px-3 py-2 rounded-md border border-gray-300 hover:bg-gray-50 transition-colors"
                    >
                      {reason.label}
                    </button>
                  ))}
                </div>

                <div className="flex space-x-3">
                  <button
                    onClick={() => setShowReportModal(false)}
                    className="flex-1 btn btn-secondary"
                  >
                    Cancel
                  </button>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default Chat;
