import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useAuth } from '../context/AuthContext';
import { useForm } from 'react-hook-form';
import { 
  FiUser, 
  FiMail, 
  FiPhone, 
  FiHeart, 
  FiEdit3, 
  FiSave,
  FiX,
  FiCalendar,
  FiActivity
} from 'react-icons/fi';
import LoadingSpinner from '../components/LoadingSpinner';

const Profile = () => {
  const { user, updateProfile } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedInterests, setSelectedInterests] = useState(user?.interests || []);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    setError
  } = useForm({
    defaultValues: {
      gender: user?.gender || '',
      preferredGender: user?.preferredGender || 'any'
    }
  });

  const interestOptions = [
    'Music', 'Movies', 'Sports', 'Gaming', 'Technology', 'Travel',
    'Food', 'Art', 'Books', 'Photography', 'Fitness', 'Fashion',
    'Science', 'Politics', 'Business', 'Education', 'Health', 'Nature'
  ];

  const toggleInterest = (interest) => {
    setSelectedInterests(prev => 
      prev.includes(interest)
        ? prev.filter(i => i !== interest)
        : [...prev, interest]
    );
  };

  const onSubmit = async (data) => {
    setIsLoading(true);
    try {
      const updateData = {
        ...data,
        interests: selectedInterests
      };

      const result = await updateProfile(updateData);
      if (result.success) {
        setIsEditing(false);
      } else {
        setError('root', { message: result.error });
      }
    } catch (error) {
      setError('root', { message: 'An unexpected error occurred' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    setIsEditing(false);
    setSelectedInterests(user?.interests || []);
    reset({
      gender: user?.gender || '',
      preferredGender: user?.preferredGender || 'any'
    });
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="min-h-screen py-12 px-4">
      <div className="container mx-auto max-w-4xl">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          {/* Header */}
          <div className="text-center mb-8">
            <h1 className="text-3xl md:text-4xl font-bold text-white mb-2">
              Your Profile
            </h1>
            <p className="text-gray-200">
              Manage your account settings and preferences
            </p>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Profile Card */}
            <div className="lg:col-span-1">
              <div className="bg-white rounded-lg shadow-lg p-6">
                <div className="text-center">
                  <div className="w-20 h-20 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                    <FiUser className="text-3xl text-blue-600" />
                  </div>
                  <h2 className="text-xl font-semibold text-gray-900 mb-1">
                    {user?.email?.split('@')[0] || 'Anonymous User'}
                  </h2>
                  <p className="text-gray-500 capitalize">
                    {user?.gender || 'Not specified'}
                  </p>

                  <div className="mt-4 pt-4 border-t border-gray-200">
                    <div className="flex items-center justify-center space-x-4 text-sm text-gray-600">
                      <div className="flex items-center space-x-1">
                        <FiCalendar className="text-sm" />
                        <span>Joined {formatDate(user?.createdAt)}</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Quick Stats */}
              <div className="bg-white rounded-lg shadow-lg p-6 mt-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  Quick Stats
                </h3>
                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <span className="text-gray-600">Status</span>
                    <span className="flex items-center space-x-1">
                      <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                      <span className="text-green-600 font-medium">Online</span>
                    </span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-gray-600">Account Type</span>
                    <span className="text-blue-600 font-medium">Standard</span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-gray-600">Verification</span>
                    <span className={`font-medium ${user?.verified ? 'text-green-600' : 'text-yellow-600'}`}>
                      {user?.verified ? 'Verified' : 'Pending'}
                    </span>
                  </div>
                </div>
              </div>
            </div>

            {/* Profile Details */}
            <div className="lg:col-span-2">
              <div className="bg-white rounded-lg shadow-lg p-6">
                <div className="flex items-center justify-between mb-6">
                  <h3 className="text-xl font-semibold text-gray-900">
                    Profile Information
                  </h3>
                  {!isEditing ? (
                    <button
                      onClick={() => setIsEditing(true)}
                      className="btn btn-primary btn-sm flex items-center space-x-1"
                    >
                      <FiEdit3 className="text-sm" />
                      <span>Edit Profile</span>
                    </button>
                  ) : (
                    <div className="flex space-x-2">
                      <button
                        onClick={handleCancel}
                        className="btn btn-secondary btn-sm flex items-center space-x-1"
                      >
                        <FiX className="text-sm" />
                        <span>Cancel</span>
                      </button>
                    </div>
                  )}
                </div>

                {isEditing ? (
                  <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
                    {/* Gender Field */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Gender
                      </label>
                      <select
                        {...register('gender', { required: 'Please select your gender' })}
                        className="input"
                      >
                        <option value="">Select your gender</option>
                        <option value="male">Male</option>
                        <option value="female">Female</option>
                        <option value="other">Other</option>
                      </select>
                      {errors.gender && (
                        <p className="mt-1 text-sm text-red-500">{errors.gender.message}</p>
                      )}
                    </div>

                    {/* Preferred Gender Field */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Preferred Chat Partner
                      </label>
                      <select
                        {...register('preferredGender')}
                        className="input"
                      >
                        <option value="any">Anyone</option>
                        <option value="male">Male</option>
                        <option value="female">Female</option>
                        <option value="other">Other</option>
                      </select>
                    </div>

                    {/* Interests */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-3">
                        Interests
                      </label>
                      <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
                        {interestOptions.map((interest) => (
                          <button
                            key={interest}
                            type="button"
                            onClick={() => toggleInterest(interest)}
                            className={`px-3 py-2 text-sm rounded-md border transition-colors ${
                              selectedInterests.includes(interest)
                                ? 'bg-blue-100 border-blue-300 text-blue-700'
                                : 'bg-gray-50 border-gray-300 text-gray-700 hover:bg-gray-100'
                            }`}
                          >
                            {interest}
                          </button>
                        ))}
                      </div>
                    </div>

                    {/* Error Message */}
                    {errors.root && (
                      <div className="bg-red-50 border border-red-200 rounded-md p-3">
                        <p className="text-sm text-red-600">{errors.root.message}</p>
                      </div>
                    )}

                    {/* Submit Button */}
                    <button
                      type="submit"
                      disabled={isLoading}
                      className="btn btn-primary disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
                    >
                      {isLoading ? (
                        <>
                          <LoadingSpinner size="sm" color="white" />
                          <span>Saving...</span>
                        </>
                      ) : (
                        <>
                          <FiSave className="text-sm" />
                          <span>Save Changes</span>
                        </>
                      )}
                    </button>
                  </form>
                ) : (
                  <div className="space-y-6">
                    {/* Account Information */}
                    <div>
                      <h4 className="text-lg font-medium text-gray-900 mb-4">
                        Account Information
                      </h4>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                          <FiMail className="text-gray-400" />
                          <div>
                            <p className="text-sm text-gray-500">Email</p>
                            <p className="font-medium text-gray-900">{user?.email}</p>
                          </div>
                        </div>

                        {user?.phone && (
                          <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                            <FiPhone className="text-gray-400" />
                            <div>
                              <p className="text-sm text-gray-500">Phone</p>
                              <p className="font-medium text-gray-900">{user.phone}</p>
                            </div>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Chat Preferences */}
                    <div>
                      <h4 className="text-lg font-medium text-gray-900 mb-4">
                        Chat Preferences
                      </h4>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                          <FiUser className="text-gray-400" />
                          <div>
                            <p className="text-sm text-gray-500">Your Gender</p>
                            <p className="font-medium text-gray-900 capitalize">
                              {user?.gender || 'Not specified'}
                            </p>
                          </div>
                        </div>

                        <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                          <FiHeart className="text-gray-400" />
                          <div>
                            <p className="text-sm text-gray-500">Preferred Partner</p>
                            <p className="font-medium text-gray-900 capitalize">
                              {user?.preferredGender === 'any' ? 'Anyone' : user?.preferredGender || 'Anyone'}
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Interests */}
                    <div>
                      <h4 className="text-lg font-medium text-gray-900 mb-4">
                        Your Interests
                      </h4>
                      {user?.interests && user.interests.length > 0 ? (
                        <div className="flex flex-wrap gap-2">
                          {user.interests.map((interest) => (
                            <span
                              key={interest}
                              className="px-3 py-1 bg-blue-100 text-blue-700 rounded-full text-sm"
                            >
                              {interest}
                            </span>
                          ))}
                        </div>
                      ) : (
                        <p className="text-gray-500 italic">
                          No interests selected. Add some to find like-minded people!
                        </p>
                      )}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default Profile;
