import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  FiMessageCircle, 
  FiShield, 
  FiUsers, 
  FiZap, 
  FiEye, 
  FiHeart,
  FiArrowRight,
  FiStar,
  FiPlay,
  FiGlobe,
  FiLock
} from 'react-icons/fi';

const Home = () => {
  const features = [
    {
      icon: FiMessageCircle,
      title: 'Anonymous Chatting',
      description: 'Chat with strangers without revealing your identity. Complete privacy guaranteed.'
    },
    {
      icon: FiShield,
      title: 'Safe & Secure',
      description: 'Advanced moderation and reporting system to ensure a safe chatting environment.'
    },
    {
      icon: FiUsers,
      title: 'Gender Filtering',
      description: 'Choose your preferred gender to chat with for more comfortable conversations.'
    },
    {
      icon: FiZap,
      title: 'Instant Matching',
      description: 'Get connected with someone instantly. No waiting, just start chatting!'
    },
    {
      icon: FiEye,
      title: 'No Registration Required',
      description: 'Quick sign-up process. Start chatting within seconds of joining.'
    },
    {
      icon: FiHeart,
      title: 'Interest Matching',
      description: 'Find people with similar interests for more engaging conversations.'
    }
  ];

  const stats = [
    { number: '10K+', label: 'Active Users' },
    { number: '50K+', label: 'Chats Daily' },
    { number: '99.9%', label: 'Uptime' },
    { number: '24/7', label: 'Moderation' }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="relative py-20 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-blue-600 via-purple-600 to-blue-800"></div>
        <div className="absolute inset-0 bg-black opacity-20"></div>

        <div className="relative container mx-auto px-4 text-center text-white">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              Chat Anonymously with
              <span className="block text-yellow-300">Random Strangers</span>
            </h1>
            <p className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto text-gray-200">
              Connect with people from around the world in a safe, anonymous environment. 
              No judgments, just genuine conversations.
            </p>

            <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
              <Link
                to="/register"
                className="btn btn-lg bg-yellow-500 hover:bg-yellow-600 text-black font-semibold px-8 py-4 rounded-full transition-all duration-300 transform hover:scale-105 flex items-center space-x-2"
              >
                <span>Start Chatting Now</span>
                <FiArrowRight />
              </Link>
              <Link
                to="/about"
                className="btn btn-lg bg-transparent border-2 border-white text-white hover:bg-white hover:text-gray-900 px-8 py-4 rounded-full transition-all duration-300"
              >
                Learn More
              </Link>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-16 bg-white">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-8">
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className="text-center"
              >
                <div className="text-3xl md:text-4xl font-bold text-blue-600 mb-2">
                  {stat.number}
                </div>
                <div className="text-gray-600 font-medium">{stat.label}</div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              Why Choose Anonymous Talk?
            </h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              We've built the safest and most user-friendly anonymous chat platform 
              with features that matter to you.
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {features.map((feature, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-300"
              >
                <div className="flex items-center mb-4">
                  <div className="p-3 bg-blue-100 rounded-lg mr-4">
                    <feature.icon className="text-2xl text-blue-600" />
                  </div>
                  <h3 className="text-xl font-semibold text-gray-900">
                    {feature.title}
                  </h3>
                </div>
                <p className="text-gray-600 leading-relaxed">
                  {feature.description}
                </p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* How It Works Section */}
